let start = 0;  // Initial page
const limit = 100;  // Limit to fetch 500 profiles (adjust as needed)
const batchSize = 10;  // Google Search returns 100 results per page
const output = [];

// Loop through pages by increments of 100
while (start < limit) {
  output.push({ json: { start: start } });
  start += batchSize;  // Increment by 100 for each page
}

return output;

// Get the items array from the response
