// Create an empty array to store the extracted URLs
const output = [];

if (items && items.length > 0) {
  // Loop over each item in the array and extract the 'link'
  for (const item of items) {
    if (item.link) {
      // Push each extracted URL as a separate item in the output
      output.push({
        json: {
          url: item.link,
        }
      });
    }
  }
}

return output;  // Return the array of URL items

