SELECT ls.topic_name, ls.topic_id
FROM learning_sessions ls
WHERE ls.user_id = {{ $('Telegram Trigger').item.json.callback_query.from.id }}
    AND ls.start_time::date = CURRENT_DATE - INTERVAL '1 day'
ORDER BY ls.start_time DESC
LIMIT 1;

